import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Button } from "./Button";
import "./Navbar.css";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
function Navbar() {
  const [hamburgerExpanded, setHamburgerExpanded] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => {
    setHamburgerExpanded(!hamburgerExpanded);
  };
  const closeMobileMenu = () => {
    setHamburgerExpanded(false);
  };

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  });

  window.addEventListener("resize", showButton);

  return (
    <>
      <nav className="navbar">
        <div className="navbar-container">
          <Link to="/" className="navbar-logo">
            Junghahn <i icon="fas fa-typo3" />
          </Link>
          <div className="menu-icon" onClick={handleClick}>
            <i
              className={hamburgerExpanded ? "fas fa-times" : "fas fa-bars"}
            ></i>
          </div>
          <ul className={hamburgerExpanded ? "nav-menu active" : "nav-menu"}>
            <li className="nav-item">
              <Link to="/Home" className="nav-links" onClick={closeMobileMenu}>
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link
                to="/Portfolio"
                className="nav-links"
                onClick={closeMobileMenu}
              >
                Portfolio
              </Link>
            </li>
            <li className="nav-item">
              <Link
                to="/Experience"
                className="nav-links"
                onClick={closeMobileMenu}
              >
                Experience
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/About" className="nav-links" onClick={closeMobileMenu}>
                About Me
              </Link>
            </li>
          </ul>
          {button && <Button buttonStyle={"btn--outline"}>Home</Button>}
        </div>
      </nav>
    </>
  );
}

export default Navbar;
