import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Link as LinkScroll } from "react-scroll";

import "./Navbar.css";

function Navbar() {
  const [hamburgerExpanded, setHamburgerExpanded] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => {
    setHamburgerExpanded(!hamburgerExpanded);
  };
  const closeMobileMenu = () => {
    setHamburgerExpanded(false);
  };

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  });

  window.addEventListener("resize", showButton);

  return (
    <>
      <nav className="navbar">
        <div className="navbar-container">
          <Link to="/" className="navbar-logo">
            Erik Junghahn <i className="fa-solid fa-mug-hot"></i>
          </Link>
          <div className="menu-icon" onClick={handleClick}>
            <i
              className={hamburgerExpanded ? "fas fa-times" : "fas fa-bars"}
            ></i>
          </div>
          <ul className={hamburgerExpanded ? "nav-menu active" : "nav-menu"}>
            <li className="nav-item">
              <Link to="/Home" className="nav-links" onClick={closeMobileMenu}>
                Home
              </Link>
            </li>
            <li className="nav-item">
              <Link
                to="/Portfolio"
                className="nav-links"
                onClick={closeMobileMenu}
              >
                Portfolio
              </Link>
            </li>
            <li className="nav-item">
              <Link
                to="/Experience"
                className="nav-links"
                onClick={closeMobileMenu}
              >
                Experience
              </Link>
            </li>
            <li className="nav-item">
              <Link to="/About" className="nav-links" onClick={closeMobileMenu}>
                About Me
              </Link>
            </li>
          </ul>
        </div>
        <div className="portfolio_navbar_wrapper">
          <ul
            className="portfolio_navbar"
            style={{
              display: "flex",
              listStyle: "none",
              justifyContent: "space-around",
            }}
          >
            <li>
              <LinkScroll to="plainsight" spy={true} smooth={true}>
                <div className="app_btn_container">
                  <img
                    className="title_icon"
                    src={"/images/plainsightlogo.png"}
                    alt="icon"
                  ></img>
                  <h1 className="app_btn_title">Plain Sight</h1>
                </div>
              </LinkScroll>
            </li>
            <li>
              <LinkScroll to="vegify" spy={true} smooth={true}>
                <div className="app_btn_container">
                  <img
                    className="title_icon"
                    src={"/images/vegifyicon.png"}
                    alt="icon"
                  ></img>
                  <h1 className="app_btn_title">Vegify</h1>
                </div>
              </LinkScroll>
            </li>
            <li>
              <LinkScroll to="quantify" spy={true} smooth={true}>
                <div className="app_btn_container">
                  <img
                    className="title_icon"
                    src={"/images/quantifyicon.png"}
                    alt="icon"
                  ></img>
                  <h1 className="app_btn_title">Quantify</h1>
                </div>
              </LinkScroll>
            </li>
            <li>
              <LinkScroll></LinkScroll>
            </li>
          </ul>
        </div>
      </nav>
    </>
  );
}

export default Navbar;
